import base64
import json
import requests
import jwt
from cryptography import x509
from cryptography.hazmat.backends import default_backend

def verify_receipt(base64_receipt: str, is_sandbox: bool = False) -> dict:
    """
    验证苹果收据
    """
    url = "https://sandbox.itunes.apple.com/verifyReceipt" if is_sandbox else "https://buy.itunes.apple.com/verifyReceipt"

    request_body = {
        "receipt-data": base64_receipt,
        "password": "fd014432952249008c1e1531e0cc696a"  # 订阅必须传 shared_secret
    }

    try:
        response = requests.post(url, json=request_body, headers={"Content-Type": "application/json"})
        result = response.json()
        # 自动重试沙盒环境
        if result.get("status") == 21007:
            print("调用到了沙盒环境，自动重试")
            return verify_receipt(base64_receipt, True)
        return result
    except Exception as e:
        print(f"验证收据失败: {e}")
        return {}


def parse_signed_payload(signed_payload: str) -> dict:
    """
    解析并验证苹果返回的 JWS Payload
    """
    try:
        # 1. 拆解 JWS 三部分
        parts = signed_payload.split(".")
        header_json = base64.urlsafe_b64decode(parts[0] + "===").decode("utf-8")
        payload_json = base64.urlsafe_b64decode(parts[1] + "===").decode("utf-8")
        header = json.loads(header_json)
        x5c = header.get("x5c", [])
        if not x5c:
            raise ValueError("Header 中没有证书链")

        # 2. 构造 Apple 公钥
        first_cert = x5c[0]
        public_key_pem = f"-----BEGIN CERTIFICATE-----\n{first_cert}\n-----END CERTIFICATE-----"
        cert = x509.load_pem_x509_certificate(public_key_pem.encode("utf-8"), default_backend())
        public_key = cert.public_key()

        # 3. 验签并解析 Payload
        decoded = jwt.decode(
            signed_payload,
            public_key,
            algorithms=["ES256"],
            audience="appstoreconnect-v1",
            options={"verify_aud": False}  # Apple 的 JWS 有时不带 aud，可以关闭验证
        )
        return decoded
    except Exception as e:
        print(f"解析失败: {e}")
        return {}


